#include <bits/stdc++.h>
using namespace std;

int main()
{
    int t;
    cin >> t;
    while (t)
    {
        long int n, k;
        cin >> n >> k;
        long int arr[n];
        for (long int i = 0; i < n; i++)
        {
            cin >> arr[i];
        }
        if (k > n)
        {
            k = k % n;
        }
        long int temp[k];
        for (long int i = 0; i < k; i++)
        {
            temp[i] = arr[n - k + i];
        }

        for (long int i = n - 1; i >= k; i--)
        {
            arr[i] = arr[i - k];
        }
        for (long int i = 0; i < k; i++)
        {
            arr[i] = temp[i];
        }

        for (long int i = 0; i < k; i++)
        {
            cout << temp[i] << ' ';
        }
        cout << endl;
        for (long int i = 0; i < n; i++)
        {
            cout << arr[i] << ' ';
        }
        cout << endl;
        t--;
    }
    return 0;
}